import React, { useEffect, useState } from 'react';
import html2pdf from 'html2pdf.js';
import './ViewDetails.css';

const ViewDetails = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('usersdatatoken');
        const response = await fetch('/validuser', {
          method: 'GET',
          headers: {
            Authorization: token,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setUserData(data.ValidUserOne);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, []);

  const downloadAsPdf = () => {
    const element = document.getElementById('pdf-container');
    const opt = {
      margin:       0,
      filename:     'user_resume.pdf',
      image:        { type: 'jpeg', quality: 0.98 },
      html2canvas:  { scale: 2 },
      jsPDF:        { unit: 'mm', format: 'a4', orientation: 'portrait' }
    };
    html2pdf().from(element).set(opt).outputPdf(pdf => {
      const blob = new Blob([pdf], { type: 'application/pdf' });
      const link = document.createElement('a');
      link.href = window.URL.createObjectURL(blob);
      link.download = opt.filename;
      link.click();
    });
  };

  if (!userData) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <button onClick={downloadAsPdf}>Download as PDF</button>
      <div className="resume-container" id="pdf-container">
        <div className="personal-details">
          <h2><div className="name">{userData.fname}</div></h2>
          <p><div className="personal-subhead"> {userData.email}</div></p>
          <p> <div className="personal-subhead"> {userData.phoneNumber} </div> </p>
          {userData.profilePic && (
            <img
              src={userData.profilePic}
              alt="Profile Pic"
              className="profile-pic"
            />
          )}

					
        </div>
      </div>
    </div>
  );
};

export default ViewDetails;
